(function($){
    $.fn.extend({
        form:function(options){
            var defaults={
                phpLocation: 'post.php'
            };
            options=$.extend(defaults,options);
            
            var form=$(this).find('form');
            
            form.submit(function () {
                alert("click");
                return false;
            });
        }
    })
})(jQuery);